resources.string:
class Buku {
private:
    string judul;
    string penulis;
public:
    void setData(string j, string p) {
        judul = j;
        penulis = p;
    }
    void tampilkan() {
        cout << "Judul: " << judul << ", Penulis: " << penulis << endl;
    }
};

int main() {
    Buku b;
    b.setData("Pemrograman C++", "Tiyandwi");
    b.tampilkan();
    return 0;
}

class Mahasiswa {
private:
    string nama;
    int umur;
public:
    Mahasiswa(string n, int u) {
        nama = n;
        umur = u;
        cout << "Mahasiswa " << nama << " dibuat." << endl;
    }
    void tampilkan() {
        cout << "Nama: " << nama << ", Umur: " << umur << endl;
    }
    ~Mahasiswa() {
        cout << "Mahasiswa " << nama << " dihapus." << endl;
    }
};

int main() {
    Mahasiswa m1("Tiyandwi", 21);
    m1.tampilkan();
    return 0;
}
